'use strict';

var minterjsTx = require('minterjs-tx');
var utils = require('../utils.js');

/**
 * @constructor
 */
function LockStakeTxData( /* {}, options */
) {
  this.txData = new minterjsTx.TxDataLockStake({});
  utils.proxyNestedTxData(this);
}

/**
 * @return {LockStakeTxData}
 */
LockStakeTxData.fromBufferFields = function fromBufferFields() {
  return new LockStakeTxData();
};

/**
 * @param {Buffer|string} [data]
 * @return {LockStakeTxData}
 */
LockStakeTxData.fromRlp = function fromRlp(data) {
  return LockStakeTxData.fromBufferFields(new minterjsTx.TxDataLockStake(data));
};

module.exports = LockStakeTxData;
